import { useEffect, useState } from "react"

export default function Letters(){
    const [state, setState] = useState(null); 
    
    useEffect(()=>{
        fetch('./db.json')
        .then((res) => res.json())
        .then((res) => {
            setState(res);
            console.log(res)
         } )    

    }, [])
    



   
    return (
        
        <div >
            <h1>YOU HAVE GOT SOME MESSAGES</h1>
           { state === null ? 'Loading' : state.messages.map((message)=>{
               return(
                   <div key = {message.id} style = {{border: 'solid black 1px'}}>

                       <h3>{message.date}</h3>
                       <h2 style = {{color: message.nameColor}}>{message.name}</h2>
                       <p style = {{color: message.textColor}}>{message.text}</p>

                   </div>
               )
           })         }
        </div>
    )
}
