import { useState } from "react"
import { Outlet } from "react-router-dom";

function SignInForm({state}){

    
    const users = state.users

    let [signInInfo, setSignInInfo] = useState({
        email: '',
        password: ''
    })


    function handleInputChange (eve){
        const target = eve.target;
        const value = target.value;
        const name = target.name;
        
        
        setSignInInfo({
            ...signInInfo,    
            [name]: value,
        })   
    }

    function handleSubmit(users, userinfo){
        users.forEach((user)=>{
        if(user.email === userinfo.email && user.password === userinfo.password){
            alert('You are signed in')
        }})
    }


    return(
        <form onSubmit={(e)=>{
            handleSubmit(users, signInInfo);
            e.preventDefault();
        }}>

            <label>
                Please write your Email. {'         '}
                <input type = 'email' name = 'email' onChange={handleInputChange} >
                </input>
            </label>
            <br />
            <label>
                Please write your password. {'          '}
                <input type = 'password' name = 'password' onChange={handleInputChange} >
                </input>
            </label>
            <br />

            <button type = 'submit'>
                Submit
            </button>
            <Outlet />
        </form>
        
    )
}

export default SignInForm;
