import { useEffect, useState } from "react"

export default function Letters(){
    const [state, setState] = useState(null); 
    
    useEffect(()=>{
        fetch('./db.json')
        .then((res) => res.json())
        .then((res) => {
            setState(res);
            console.log(res)
         } )    

    }, [])
    



   
    return (
        <div >
           { state === null ? 'Loading' : state.messages.map((message)=>{
               return(
                   <div key = {message.id} style = {{color: 'red'}}>
                       <h3>{message.date}</h3>
                       <h2>{message.name}</h2>
                       <p>{message.text}</p>
                   </div>
               )
           })         }
        </div>
    )
}
